package com.ben.demo.data.consumer;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;import org.springframework.cloud.netflix.feign.EnableFeignClients;import org.springframework.web.servlet.config.annotation.EnableWebMvc;/** * Created by ben on 2017/11/27. */@EnableFeignClients@EnableEurekaClient@EnableWebMvc@SpringBootApplicationpublic class ConsumerApplication {    public static void main(String[] args) {        SpringApplication.run(ConsumerApplication.class, args);    }}