package com.ben.demo.data.provider;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;import org.springframework.web.servlet.config.annotation.EnableWebMvc;/** * Created by ben on 2017/11/27. */@EnableEurekaClient@EnableWebMvc@SpringBootApplicationpublic class DataProviderApplication {    public static void main(String[] args) {        SpringApplication.run(DataProviderApplication.class, args);    }}